        jTextField2.setText(null);
        jTextField3.setText(null);
        jTextField4.setText(null);
        jTextField5.setText(null);
        buttonGroup1.clearSelection();
        ComboBoxAgama.setSelectedIndex(0);
        CekBoxDataSudahBenar.setSelected(false);
